import logo from './Фото 03.09.2024, 23.16.jpg';
import './App.css';

export default function App() {
  return (
    <div>
      <p>Hi</p>
      <canvas id="canvas"></canvas>
      <MiniGame />
    </div>
  );
}

function MiniGame(){
  let canvas = $("#canvas")[0].getContext("2d");
    let x = 75;
    let y = 75;
    let dx = 2;
    let dy = 4;
    let HEIGHT = $("#canvas").height();
    let WIDTH = $("#canvas").width();
    let paddlex = WIDTH/2;
    let paddleh = 10;
    let paddlew = 75;
    let rightArrow = false;
    let leftArrow = false;
    let intervalId;
    function init(){
        intervalId = setInterval(draw,10);
    }
    function clear(){
        canvas.clearRect(0,0,400,400);
    }
    function circle(x,y,r){
        canvas.beginPath();
        canvas.arc(x,y,r,0,Math.PI*2,true);
        canvas.closePath();
        canvas.fill();
    }
    function rect(x,y,w,h){
        canvas.beginPath();
        canvas.rect(x,y,w,h);
        canvas.closePath();
        canvas.fill();
    }
    function onKeyDown(evt){
        if(evt.keyCode==39) rightArrow=true;
        else if(evt.keyCode==37) leftArrow=true;
    }
    function onKeyUp(evt){
        if(evt.keyCode==39) rightArrow=false;
        else if(evt.keyCode==37) leftArrow=false;
    }
    $(document).keydown(onKeyDown);
    $(document).keyup(onKeyUp);

    function draw(){
        clear();
        circle(x,y,10);

        if(rightArrow) paddlex+=5;
        else if(leftArrow) paddlex-=5;
        rect(paddlex, HEIGHT-paddleh, paddlew, paddleh);

        if(x+dx>WIDTH || x+dx<0)
            dx = -dx;
        if(y+dy<0)
            dy=-dy;
        else if(y+dy>HEIGHT){
            if (x > paddlex && x < paddlex + paddlew)
                dy = -dy;
            else {
                //game over, so stop the animation
                canvas.fillStyle="Black";
                canvas.font = "40 fantasy";
                canvas.fillText("Game Over",160,160);
                canvas.fill();
                clearInterval(intervalId);
                return;
            }
        }
        x+=dx;
        y+=dy;
    }
    init();
}
